package life.forever.cf.entry;import android.os.Parcel;import android.os.Parcelable;public class TagBean implements Parcelable {    public String id;    public String tag;    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeString(this.id);        dest.writeString(this.tag);    }    public TagBean() {    }    protected TagBean(Parcel in) {        this.id = in.readString();        this.tag = in.readString();    }    public static final Parcelable.Creator<Task> CREATOR = new Parcelable.Creator<Task>() {        @Override        public Task createFromParcel(Parcel source) {            return new Task(source);        }        @Override        public Task[] newArray(int size) {            return new Task[size];        }    };}