package life.forever.cf.entry;import java.io.Serializable;import java.util.List;public class TagsBean implements Serializable {    public String serverNo;    public long serverTime;    public ResultData resultData;    public class ResultData implements Serializable {        public String msg;        public long status;        public List<Tags> tags;        public class Tags implements Serializable {            public String addtime;            public String status;            public String id;            public String tag;            public String pinyin;            public String content_type;            public boolean isSelected = false;            public Tags(){}            public Tags(String addtime, String status, String id, String tag, String pinyin, String content_type) {                this.addtime = addtime;                this.status = status;                this.id = id;                this.tag = tag;                this.pinyin = pinyin;                this.content_type = content_type;            }            public boolean isSelected() {                return isSelected;            }            public void setSelected(boolean isSelected) {                this.isSelected = isSelected;            }        }    }}